<?php

require_once './Solution.php';

use PHPUnit\Framework\TestCase;

final class SolutionTest extends TestCase
{
    public function testSolution()
    {
        $s = new Solution();
        $this->assertTrue($s->isSubsequence('abc', 'ahbgdc'));
        $this->assertFalse($s->isSubsequence('axc', 'ahbgdc'));
        $this->assertTrue($s->isSubsequence('leeeeetcode', ""));
    }
}
