<?php

require_once './Solution.php';

use PHPUnit\Framework\TestCase;

final class SolutionTest extends TestCase
{
    public function testSolution()
    {
        $s = new Solution();
        $this->assertEquals(7, $s->longestPalindrome('abccccdd'));
        $this->assertEquals(4, $s->longestPalindrome('aabb'));
        $this->assertEquals(5, $s->longestPalindrome('aabbc'));
        $this->assertEquals(3, $s->longestPalindrome('ccc'));
        $this->assertEquals(5, $s->longestPalindrome('bananas'));
        $this->assertEquals(983, $s->longestPalindrome(''));
    }
}
